import React from 'react';
import '../../App.css';

export default function CartTotals({value}) {
    const {itemsTotal, salesTax, cartTotal, clearCart} = value;
    return (
        <React.Fragment>
            <div className='container'>
                <div className='row'>
                    <div className='col-10 mx-auto text-right'>
                        <h5>Total <span>${itemsTotal}</span></h5>
                        <h5>Sales tax <span>${salesTax}</span></h5>
                        <h5>Total cost <span>${cartTotal}</span></h5>
                    </div>
                </div>

            </div>
        </React.Fragment>
    )
}
