import React from 'react';
import styled from 'styled-components';
import {Link} from 'react-router-dom';
import Selected from '../Selected/Selected';
import Summer from '../Summer/Summer';
import Footer from '../Footer/Footer';
import {ProductConsumer} from '../../context';
import Navbar from '../Navbar/Navbarpages';
import Breadcrumbs from '../Breadcrumbs/Breadcrumbs';



class Details extends React.Component {
	render() {
		return (
			<React.Fragment>
				<Navbar />
                <ProductConsumer>
				<Breadcrumbs />
					{value => {
						const {id, img, title, price, info, inCart, size, sign} = value.detailProduct;
						return (
							<div className='container py-5'>
								{/* title */}
								<div className='row'>
									<div className='col-10 mx-auto text-center text-slanted my-5'>
										<h2>{title}</h2>
									</div>
								</div>

								{/* product info */}
								<div className='row'>
									<div className='col-10 mx-auto col-md-6 my-3'>
										<img src={img} className='img-fluid' alt='product'/>
									</div>


									<div className='col-10 mx-auto col-md-6 my-3'>
										<h1 className="font-weight-light">{title}</h1>
										<h1 className='mb-5'>${price}</h1>
										<p className="font-weight-light mb-3">Size: {size}
											</p>
										<p className='font-weight-semibold mb-3'>See colort chart</p>
											{/* add to cart button */}

											<AddToCart className='button mb-4 size-lg text-uppercase' 
											disabled={inCart?true:false} onClick={() => {
												value.addToCart(id);
												value.openModal(id);
											}} >
											{inCart ? 'in cart' : 'add to cart'}
											</AddToCart>
											<p className="font-weight-bold mb-1">Standart shipment</p>
											<p className="font-weight-light mb-3">Free within 3-6 business days</p>
											<p className="font-weight-bold mb-1">Express delivery</p>
											<p className="font-weight-light mb-3">$35,00 available</p>
										<p className='text-muted font-weight-light'>Product ID: {sign}</p>
									</div>
								</div>
							</div>
						);
					}}
				</ProductConsumer>
		        <Selected />
		        <Summer />
		        <Footer />
      		</React.Fragment>
		)
	}
}

const AddToCart = styled.button`

	border: transparent;
	background-color: var(--mainYellow);
	border-radius: 2rem;
	width: 13rem;
	height: 3rem;
	font-size: 0.8rem;
	font-weight: 700;
	&:focus {
		outline: none;
	}
	&:active {
		background-color: var(--secondYellow);
	}

`

export default Details;
