import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import logo from '../../logo.svg';
import cart from '../../cart.svg';
import '../../App.css';
import Dropdown from './Dropdown';

function Navbar() {
	const [click, setClick] = useState(false);
	const [dropdown, setDropdown] = useState(false);

	const handleClick = () => setClick(!click);
	const closeMobileMenu = () => setClick(false);

	const onMouseEnter = () => {
	  if (window.innerWidth < 960) {
		setDropdown(false);
	  } else {
		setDropdown(true);
	  }
	};

	const onMouseLeave = () => {
	  if (window.innerWidth < 960) {
		setDropdown(false);
	  } else {
		setDropdown(false);
	  }
	};
		return (
			<div className='container'>
				<div className='row'>
					<div className='col'>
						<div className="navbar navbar-expand-sm px-sm-5 mt-5 py-0">
							<Link to='/' onClick={closeMobileMenu}>
								<img src={logo} alt='brand' className='brand-logo' />
							</Link>
							<div className='menu-icon' onClick={handleClick}>
          						<i className={click ? 'fas fa-times' : 'fas fa-bars'} />
        					</div>

							<ul className={click ? 'navbar-nav align-items-center mx-auto active' : 
												'navbar-nav align-items-center mx-auto'} >
								<li className="nav-item ml-5"
									onMouseEnter={onMouseEnter}
									onMouseLeave={onMouseLeave}
									>
									<Link 
									className='nav-links'
									onClick={closeMobileMenu}
									 >Men</Link>
									 {dropdown && <Dropdown />}
								</li>
								<li className="nav-item ml-5">
									<Link to='/women' className='nav-link'>Women</Link>
								</li>
								<li className="nav-item ml-5">
									<Link to='/kids' className='nav-link'>Kids</Link>
								</li>
								<li className="nav-item ml-5">
									<Link to='/sale' className='nav-link'>Sale</Link>
								</li>
							</ul>
							<Link to='/shoppingcart' className='ml-auto'>
									<img src={cart} alt='cart' className='cart' />
							</Link>
						</div>
					</div>
				</div>
			</div> 
		)
	}




export default Navbar;
