import React from 'react';
import styled from 'styled-components';
import {Link} from 'react-router-dom';
import Selected from '../Selected/Selected';
import Subscribe from '../Subscribe/Subscribe';
import Footer from '../Footer/Footer';
import {ProductConsumer} from '../../context';


class Details extends React.Component {
	render() {
		return (
			<React.Fragment>
                <ProductConsumer>
					{value => {
						const {id, img, title, price, info, inCart, size} = value.detailProduct;
						return (
							<div className='container py-5'>
								{/* title */}
								<div className='row'>
									<div className='col-6  text-center text-slanted my-5'>
										<h2>{title}</h2>
									</div>
								</div>

								{/* product info */}
								<div className='row'>
									<div className='col-6  col-md-6 mx-3'>
										<img src={img} className='img-fluid' alt='product'/>
									</div>
								</div>
								<div className='row'>
									<div className='col-10 mx-auto col-md-6 mx-3'>
										<h1 className="font-weight-light">{title}</h1>
										<h1 >{price}</h1>
										<p className="font-weight-light">Size: 
											<span className='text-muted'> {size[0]}  {size[1]}  {size[2]}  {size[3]}
										</span></p>
											{/* add to cart button */}

											<AddToCart className='button m-3 size-lg text-uppercase' 
											disabled={inCart?true:false} onClick={() => {
												value.addToCart(id)
											}} >
											{inCart ? 'in cart' : 'add to cart'}
											</AddToCart>
									</div>
								</div>
							</div>
						);
					}}
				</ProductConsumer>
		        <Selected />
		        <Subscribe />
		        <Footer />
      		</React.Fragment>
		)
	}
}

const AddToCart = styled.button`

	border: transparent;
	background-color: var(--mainYellow);
	border-radius: 2rem;
	width: 13rem;
	height: 3rem;
	font-size: 0.8rem;
	font-weight: 700;
	&:focus {
		outline: none;
	}
	&:active {
		background-color: var(--secondYellow);
	}

`

export default Details;
