              if(item.category === 'women') {
            return (
              <li key={index}>
                <Link
                  className={item.cName}
                  to={item.path}
                  onClick={() => setClick(false)}>
                  {item.title}
                </Link>
              </li>
            );
          }
          })}
        </ul>
      </>
    );
  }

export default Dropdown;
