import React from 'react';
import styled from 'styled-components';
import {Link} from 'react-router-dom';
import {ProductConsumer} from '../../context';
import PropTypes from 'prop-types';

class ShoppingCart extends React.Component {
	render() {
		const {id, title, img, price, inCart} = this.props.product;
		return (
			<React.Fragment>
			   <CardWrapper className='col-9 mx-auto col-md-6 col-lg-3 my-3'>
					<div className="card">
						<ProductConsumer>
						<div className="img-container">
							<Link to='/details'>
								<img src={img} alt='product' className='card-image-top' 
								onClick={() => console.log('clicked')}/>
							</Link>
						</div>
						</ProductConsumer>
						<div className='card footer d-flex justify-content-left'>
							<p className='m-0 mt-4'>{title}</p>
							<div className='m-0 mt-1'>{price}</div>
						</div>	
					</div>
			   </CardWrapper>
      		</React.Fragment>
		)
	}
}

ProductConsumer.propTypes = {
	product: PropTypes.shape({
		id: PropTypes.number,
		img: PropTypes.string,
		title: PropTypes.string,
		price: PropTypes.number,
		inCart: PropTypes.bool
	}).isRequired
};

const CardWrapper = styled.div`
.card {
	border-color: transparent;

}
box-sizing: content-box;
`

export default ShoppingCart;
