import React from 'react';
import {Link} from 'react-router-dom';
import '../../App.css';

export default function CartTotals({value}) {
    const {itemsTotal, salesTax, cartTotal, clearCart} = value;
    return (
        <React.Fragment>
            <div className='container'>
                <div className='row'>
                    <div className='col-10 mx-auto text-center'>
                        <Link to='/'>
                            <button className='btn btn-clear' 
                            type='button'
                            onClick={() => clearCart()}>
                                Clear Cart
                            </button>
                        </Link>
                        <h5>Total<span>{itemsTotal}</span></h5>
                        <h5>Sales tax<span>{salesTax}</span></h5>
                        <h5>Total cost<span>{itemsTotal}</span></h5>
                    </div>
                </div>

            </div>
        </React.Fragment>
    )
}
