import React, {useState } from 'react';
import { Link } from 'react-router-dom';
import logo from '../../logo.svg';
import cart from '../../cart.svg';
import '../../App.css';

class Navbar extends React.Component {

	render() {
		return (

			<div className='container'>
				<div className='row'>
					<div className='col'>
						<div className="navbar navbar-expand-sm px-sm-5 mt-5 py-0">
							<Link to='/'>
								<img src={logo} alt='brand' className='brand-logo' />
							</Link>
							<ul className='navbar-nav align-items-center mx-auto' >
								<li className="nav-item ml-5">
									<NavItem className='nav-link'>
									<DropdownMenu></DropdownMenu>
									</NavItem>
								</li>
								<li className="nav-item ml-5">
									<Link to='/women' className='nav-link'>Women</Link>
								</li>
								<li className="nav-item ml-5">
									<Link to='/kids' className='nav-link'>Kids</Link>
								</li>
								<li className="nav-item ml-5">
									<Link to='/sale' className='nav-link'>Sale</Link>
								</li>
							</ul>
							<Link to='/shoppingcart' className='ml-auto'>
									<img src={cart} alt='cart' className='cart' />
							</Link>
						</div>
					</div>
				</div>
			</div> 

		)
	}
}

function Navbars(props) {
	return (
	  <nav className="navbar navbar-expand-sm px-sm-5 mt-5 py-0">
		<ul className="navbar-nav align-items-center mx-auto">{props.children}</ul>
	  </nav>
	);
  }

  function NavItem(props) {
	const [open, setOpen] = useState(false);

	return (
	  <li className="nav-link">
		<a href="#" className="nav-link" onClick={() => setOpen(!open)}>
		  Men
		</a>

		{open && props.children}
	  </li>
	);
  }

  // Dropdown

  function DropdownMenu() {
	const [activeMenu, setActiveMenu] = useState('main');




	function DropdownItem(props) {
	  return (
		<React.Fragment>
		<a href="#" className="menu-item" onClick={() => props.goToMenu && setActiveMenu(props.goToMenu)}>
		  <span className="icon-button">{props.leftIcon}</span>
		  {props.children}
		</a>
	  </React.Fragment>
	  );
	}

	return (
		<>
		<div className='dropdown'>
		<div className='row'>
			<div className='col-3'>
				<DropdownItem><Link to='/men'>T-shirts</Link></DropdownItem>
				<DropdownItem>Shirts</DropdownItem>
				<DropdownItem>Jeans</DropdownItem>
				<DropdownItem>Shoes</DropdownItem>

				</div>
				<div className='col-3'>
				<DropdownItem><Link to='/men'>T-shirts 2</Link></DropdownItem>
				<DropdownItem>Shirts 2</DropdownItem>
				<DropdownItem>Jeans 2</DropdownItem>
				<DropdownItem>Shoes 2</DropdownItem>

				</div>
				<div className='col-3'>
				<DropdownItem><Link to='/men'>T-shirts 3</Link></DropdownItem>
				<DropdownItem>Shirts 3</DropdownItem>
				<DropdownItem>Jeans 3</DropdownItem>
				<DropdownItem>Shoes 3</DropdownItem>

				</div>
				</div>
		</div>
		  </>
	);
  }

export default Navbar;
