import React from 'react';
import Card from '../Card/Card';
import {ProductConsumer} from '../../context';
import PropTypes from 'prop-types';

class Todayproducts extends React.Component {
	render() {
		return (
			<React.Fragment>
				<div className='container selected'>
					<div className='row'>
						<div className='col-10 text-center'>
							<h2 className='selected-title'>Products in today</h2>
						</div>
					</div>
					<div className='row'>
						<div className='col-6 col-md-3'>
							<ProductConsumer>
								{value => {
									return value.products.map(product => { 
										if (product.block === 'today-1') {
											return <CardBlock key={product.id} product ={product} />;
										}
									});
								}}
							</ProductConsumer>
							</div>
						<div className='col-6 col-md-3'>
						<ProductConsumer>
								{value => {
									return value.products.map(product => { 
										if (product.block === 'selected-2') {
											return <CardBlock key={product.id} product ={product} />;
										}
									});
								}}
							</ProductConsumer>
						</div>
						<div className='col-6 col-md-3'>
						<ProductConsumer>
								{value => {
									return value.products.map(product => { 
										if (product.block === 'today-2') {
											return <CardBlock key={product.id} product ={product} />;
										}
									});
								}}
							</ProductConsumer>
						</div>
						<div className='col-6 col-md-3'>
						<ProductConsumer>
								{value => {
									return value.products.map(product => { 
										if (product.block === 'selected-4') {
											return <CardBlock key={product.id} product ={product} />;
										}
									});
								}}
							</ProductConsumer>
						</div>
					</div>
				</div>
			</React.Fragment>
		)
	}
}

export default Todayproducts;
