import React from 'react';
import '../../App.css';

export default function CartItem({item, value}) {
    const {id, title, img, size, price, total, count} = item;
    const {increment, decrement, removeItem} = value;
    return (
        <div className='row'>
            <div className='col mx-auto m-1 col=lg-2'>
                <img src={img} style={{width: '4rem', height: 'auto'}} className='image-fluid' alt='product'/>
            </div>
            <div className='col-10 mx-auto m-1 col-lg-2'>{title}</div>
            <div className='col-10 mx-auto m-1 col-lg-2'>{size}</div>
            <div className='col-10 mx-auto m-1 col-lg-2 my-2 my-lg-0'>
                <div className='d-flex justify-content-enter quintity'>
                    <div><span className='btn btn-black mx-1' onClick={() => decrement}>-</span></div>
                    <div><span className='btn  mx-1 py=0' >1</span></div>
                    <div><span className='btn btn-black mx-1' onClick={() => increment}>+</span></div>
                </div>
            </div>
            <div className='col-10 mx-auto m-1 col-lg-2'>{price}</div>
            <div className='col-10 mx-auto m-1 col-lg-2'>/\</div>
        </div>
    )
}
