import React from 'react';
import slideshow from '../../images/slideshow-img-1.png';
import '../../App.css';
import { Link } from 'react-router-dom';

class Slideshow extends React.Component {
	render() {
		return (
			<div  className='slideshow-img'>
				<div className='slideshow-text'>
					<div className='slideshow-content'>
						<h1 className='slideshow-text-1'>Sale of the summer collection</h1>
						<Link to='/men'>
							<button className='btn btn-slideshow'><span>&#8594;</span>Shop now</button>
						</Link>
					</div>
				</div>
   			</div>
		)
	}
}

export default Slideshow;
