import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css';
import Dropdown from './Dropdown';
import logo from '../../logo-color.svg';
import cart from '../../cart-pages.svg';

function Navbar() {
  const [click, setClick] = useState(false);
  const [dropdown, setDropdown] = useState(false);

  const handleClick = () => setClick(!click);
  const closeMobileMenu = () => setClick(false);

  const onMouseEnter = () => {
    if (window.innerWidth < 960) {
      setDropdown(false);
    } else {
      setDropdown(true);
    }
  };

  const onMouseLeave = () => {
    if (window.innerWidth < 960) {
      setDropdown(false);
    } else {
      setDropdown(false);
    }
  };

  return (
    <>
	<div className='container'>
		<div className='row'>
			<div className='col'>

			<nav className='navbar navbar-expand-sm px-sm-5 mt-5 pt-0 navbar-pages'>
        <Link to='/' className='navbar-logo' onClick={closeMobileMenu}>
		<img src={logo} alt='brand' className='brand-logo' />
        </Link>
        <div className='menu-icon' onClick={handleClick}>
          <i className={click ? 'fas fa-times' : 'fas fa-bars'} />
        </div>
        <ul className={click ? 'nav-menu active' : 'nav-menu'}>
          <li className='nav-item'>
            <button className='nav-links button btn' onClick={closeMobileMenu}>
              Man
            </button>
          </li>
          <li
            className='nav-item'
            onMouseEnter={onMouseEnter}
            onMouseLeave={onMouseLeave}
          >
            <button
              className='nav-links button btn'
              onClick={closeMobileMenu}
            >
              Woman
            </button>
            {dropdown && <Dropdown />}
          </li>
          <li className='nav-item'>
            <button
              to='/products'
              className='nav-links button btn'
              onClick={closeMobileMenu}
            >
              Kids
            </button>
          </li>
          <li className='nav-item'>
            <button
              to='/contact-us'
              className='nav-links button btn'
              onClick={closeMobileMenu}
            >
              Sale
            </button>
          </li>
          <li>
		  <Link to='/shoppingcart' className='ml-auto'>
				<img src={cart} alt='cart' className='cart' />
			</Link>
          </li>
        </ul>

      </nav>

			</div>
		</div>
	</div>

    </>
  );
}

export default Navbar;
