import React from 'react';
import styled from 'styled-components';
import {Link} from 'react-router-dom';
import Selected from '../Selected/Selected';
import Summer from '../Summer/Summer';
import Footer from '../Footer/Footer';
import {ProductConsumer} from '../../context';
import Navbar from '../Navbar/Navbarpages';
import Breadcrumbs from '../Breadcrumbs/Breadcrumbs';
import description from '../../images/description-product.png';
import care from '../../images/care-product.png';



class Details extends React.Component {
	render() {
		return (
			<React.Fragment>
				<Navbar />
                <ProductConsumer>
					{value => {
						const {id, img, title, price, info, inCart, size, sign} = value.detailProduct;
						return (
							<div className='container product-page'>
								<Breadcrumbs />

								{/* product info */}
								<div className='row'>
									<div className='col-10 mx-auto col-md-6 my-3'>
										<img src={img} className='img-fluid' alt='product'/>
									</div>


									<div className='col-10 mx-auto col-md-6 my-3'>
										<h2 className="font-weight-light">{title}</h2>
										<h2 className='details-price'>${price}</h2>
										<p className="details-size">Size: {size} <span><select name="size" id="size">
																					<option value="s">S</option>
																					<option value="m">M</option>
																					<option value="l">L</option>
																					<option value="xl">XL</option>
																					</select></span>
																					</p>
											{/* add to cart button */}

											<AddToCart className='button mb-4 size-lg text-uppercase' 
											disabled={inCart?true:false} onClick={() => {
												value.addToCart(id);
												value.openModal(id);
											}} >
											{inCart ? 'in cart' : 'add to cart'}
											</AddToCart>
											<p className="font-weight-bold mb-1">Standart shipment</p>
											<p className="font-weight-light mb-3">Free within 3-6 business days</p>
											<p className="font-weight-bold mb-1">Express delivery</p>
											<p className="font-weight-light mb-3">$35,00 available</p>
										<p className='text-muted font-weight-light'>Product ID: {sign}</p>
									</div>
								</div>


							<div className='container-md'>
								<div className='row'>
									<div className='col-10 mx-auto py-6'>
										<h3 className='description'>Description</h3>
									</div>
								</div>
									<div className='row'>
										<div className='col-10 col-md-6 col-lg-6 mx-auto'>
											<div className='description-content'>
												<img src={description} alt='image'/>
												<h4 className='description-title'>Details and product description</h4>
												<p className='description-text'>{info}</p>
											</div>
										</div>	

												<div className='col-10 col-md-6 col-lg-6 mx-auto'>
													<div className='material-content'>
														<img src={care} alt='image'/>
														<h4 className='material-title'>Material(s) and care</h4>
														<p className='material-text'>Body: 98% COTTON - 2% ELASTANE</p>
													</div>
												</div>
									</div>
							</div>
							</div>
						);
					}}
				</ProductConsumer>
		        <Selected />
		        <Summer />
		        <Footer />
      		</React.Fragment>
		)
	}
}

const AddToCart = styled.button`

	border: transparent;
	background-color: var(--mainYellow);
	border-radius: 2rem;
	width: 13rem;
	height: 3rem;
	font-size: 0.8rem;
	font-weight: 700;
	&:focus {
		outline: none;
	}
	&:active {
		background-color: var(--secondYellow);
	}

`

export default Details;
