import React from 'react';
import styled from 'styled-components';
import {Link} from 'react-router-dom';
import {ProductConsumer} from '../../context';
import PropTypes from 'prop-types';

class Card extends React.Component {
	render() {
		const {id, title, img, price, category} = this.props.product;
		return (
			<React.Fragment>
			   <CardWrapper className='col-9 mx-auto col-md-5 col-lg-3 my-3'>
					<div className="card">
						<ProductConsumer>
							{(value) => (
							<div className="img-container" 
							onClick={() => value.handleDetail(id)}>
							<Link to='/details'>
								<img src={img} alt='product' className='card-image'/>
							</Link>
						</div>
						)}
						</ProductConsumer>

						<div className='card footer d-flex justify-content-left'>
							<p className='m-0 mt-4'>{title}</p>
							<div className='m-0 mt-1'>${price}</div>
							<div className='m-0 mt-1'>{category}</div>
						</div>	
					</div>
			   </CardWrapper>
      		</React.Fragment>
		)
	}
}

ProductConsumer.propTypes = {
	product: PropTypes.shape({
		id: PropTypes.number,
		img: PropTypes.string,
		title: PropTypes.string,
		price: PropTypes.number,
		category: PropTypes.string,
		inCart: PropTypes.bool
	}).isRequired
};

const CardWrapper = styled.div`
.card {
	border-color: transparent;

}
box-sizing: content-box;
`

export default Card;
