import React from 'react';
import styled from 'styled-components';
import {Link} from 'react-router-dom';
import Selected from '../Selected/Selected';
import Subscribe from '../Subscribe/Subscribe';
import Footer from '../Footer/Footer';
import {ProductConsumer} from '../../context';


class Details extends React.Component {
	render() {
		return (
			<React.Fragment>
                <ProductConsumer>
					{value => {
						const {id, img, title, price, info, inCart, size} = value.detailProduct;
						return (
							<div className='container py-5'>
								{/* title */}
								<div className='row'>
									<div className='col-10 mx-auto text-center text-slanted my-5'>
										<h2>{title}</h2>
									</div>
								</div>
								{/* product info */}
								<div className='row'>
									<div className='col-10 mx-auto col-md-6 mx-3'>
										<img src={img} className='img-fluid' alt='product'/>
									</div>
								</div>
								<div className='row'>
									<div className='col-10 mx-auto col-md-6 mx-3'>
										<h1 className="font-weight-light">{title}</h1>
										<h1 >{price}</h1>
										<p className="font-weight-light">Size: 
											<span className='text-muted'> {size[0]}  {size[1]}  {size[2]}  {size[3]}
										</span></p>
										<p className='font-weight-light'>Quantity: </p>
										<div cclassName="number-controls">
											<button className="number-control left" id="subtract">-</button>
											<input type="number" id="human-guess" min={0} max={9} value={0} />
											<button className="number-control right" id="add">+</button>
											<button className='button'>
											{inCart ? 'in cart' : 'add to cart'}
										</button>
										</div>
										{/* add to cart button */}

									</div>
								</div>
							</div>
						);
					}}
				</ProductConsumer>
		        <Selected />
		        <Subscribe />
		        <Footer />
      		</React.Fragment>
		)
	}
}

export default Details;
