import React, { Component } from 'react';
import Navbar from '../Navbar/Navbarpages';
import {Link} from 'react-router-dom';

export class SubmitOrder extends Component {
    continue = e => {
        e.preventDefault();
        this.props.nextStep();
    }

     back = e => {
        e.preventDefault();
        this.props.prevStep();
    }

    render() {
        const { values: { firstName, lastName, street, apartment, city, 
            state, zipCode, email, phoneNumber } } = this.props;

        return (
            <React.Fragment>
                <Navbar />

                <div className='container summary-details'>
                    <div className='row'>
                            <div className="col-10 col-md-10 col-lg-8 mx-auto my-4 text-title">
                                <h4 className="title-summary">Summary</h4>
                            </div>
                        </div>

                    <div className='row'>
                        <div className='col-12 col-md-6 mx-auto'>
                            <ul>
                                <li>Name: {firstName}</li>
                                <li>{lastName}</li>
                                <li>{street}</li>
                                <li>{apartment}</li>
                                <li>{city}</li>
                                <li>{state}</li>
                                <li>{zipCode}</li>
                                <li>{email}</li>
                                <li>{phoneNumber}</li>
                            </ul>
                        </div>

                        <div className='col-12 col-md-6 mx-auto'>
                            <h4>Sub-total: $121</h4>
                            <h4>Sales tax: $9.45</h4>
                            <h4>Shipping rate: $35</h4>
                            <h4>Total: $165.45</h4>
                        </div>
                    </div>



                    <div className='row'>
                        <div className='col-10 col-md-4 mx-auto text-center my-2'>
                            <Link onClick={this.back} className='continue-shopping'>&#8592; Back</Link>
                        </div>

                        <div className='col-10 col-md-4 mx-auto text-center my-2'>
                        <button 
                                className='button modal-continue-btn' 
                                onClick={this.continue}>
                                Submit order
                            </button>
                        </div>
                    </div>
                </div>
            </React.Fragment>
        )
    }
}

export default SubmitOrder
