import React from 'react';
import Card from '../Card/Card';
import { ProductConsumer } from '../../context';

class Todayproducts extends React.Component {
	render() {
		return (
			<React.Fragment>
				<div className='container selected'>
					<div className='row'>
						<div className='col-10 text-center'>
							<h2 className='selected-title'>Selected just for you</h2>
						</div>
					</div>
					<div className='row'>
						<div className='col-3'>
							<ProductConsumer>
							{value => {
									return value.products.shift(product => {
										return <Card key={product.id[0]} product ={product} />;
									});
								}}
							</ProductConsumer>

							</div>
						<div className='col-3'>Card-2</div>
						<div className='col-3'>Card-3</div>
						<div className='col-3'>Card-4</div>
					</div>
				</div>
			</React.Fragment>
		)
	}
}

export default Todayproducts;
